export * as GeolocationUtils from './utils/geolocation';
export * as ImageUtils from './utils/image';
export * as StringUtils from './utils/string';

export const enable = (callback, errorCallback) => navigator.geolocation
  .watchPosition(callback, errorCallback);

export const getCurrent = (callback, errorCallback) => navigator.geolocation
  .getCurrentPosition(callback, errorCallback);

export const disable = watcher => navigator.geolocation.clearWatch(watcher);

const deg2rad = deg => deg * (Math.PI / 180);

const forceFloat = (value) => {
  if (typeof value === 'string') {
    return parseFloat(value);
  }
  return value;
};

export const distance = (lat1, lng1, lat2, lng2) => {
  const fLat1 = forceFloat(lat1);
  const fLng1 = forceFloat(lng1);
  const fLat2 = forceFloat(lat2);
  const fLng2 = forceFloat(lng2);
  // Radius is in meters
  const earthRadius = 6371000;
  const dLat = deg2rad(fLat2 - fLat1);
  const dLng = deg2rad(fLng2 - fLng1);
  const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
    Math.cos(deg2rad(fLat1)) *
    Math.cos(deg2rad(fLat2)) *
    Math.sin(dLng / 2) * Math.sin(dLng / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return Math.round(earthRadius * c);
};

export const getFileData = file => new Promise((resolve, reject) => {
  const reader = new FileReader();
  reader.addEventListener('load', ({ target }) => {
    resolve(target.result !== 'data:' ? target.result : null);
  });
  reader.addEventListener('error', error => reject(error));
  reader.readAsDataURL(file);
});

import React, { Component } from 'react';
import PropTypes from 'prop-types';
import * as ImageUtils from '../../utils/image';

class ImageFile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      imageData: null,
    };
  }

  componentDidMount() {
    const { file } = this.props;
    if (file) {
      ImageUtils.getFileData(file).then(imageData => this.setState({ imageData }));
    }
  }

  get src() {
    const { imageData } = this.state;
    return imageData;
  }

  get alt() {
    const { alt } = this.props;
    return alt;
  }

  render() {
    const { imageData } = this.state;
    const { className } = this.props;
    return imageData ? (
      <img
        className={`imageFile ${className}`}
        src={this.src}
        alt={this.alt}
      />
    ) : null;
  }
}

ImageFile.propTypes = {
  alt: PropTypes.string.isRequired,
  className: PropTypes.string.isRequired,
  file: PropTypes.object.isRequired,
};

export default ImageFile;

import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { FontAwesomeIcon as FA } from '@fortawesome/react-fontawesome';
import { faUpload } from '@fortawesome/free-solid-svg-icons';
import * as ImageUtils from '../../utils/image';

class ImageUploader extends Component {
  constructor(props) {
    super(props);

    this.state = {
      imageData: null,
    };
  }

  get hasImageClassName() {
    return this.imageSrc ? 'imageUploader--hasImage' : '';
  }

  get imageSrc() {
    const { imageData } = this.state;
    const { src } = this.props;
    return imageData || src;
  }

  setImage(e) {
    const file = e.target.files[0];
    const maxKb = this.props.fileSize || 256;
    const maxSize = maxKb * 1024;

    if (file && file.size > maxSize) {
      alert(`File cannot be greater than ${maxKb}Kb. Image will be removed.`);
      e.target.value = null;
      this.setState({ imageData: null });
      return;
    }
    this.setImageData(file);
  }

  setImageData(file) {
    if (file) {
      ImageUtils.getFileData(file).then(imageData => this.setState({ imageData }));
    }
  }

  render() {
    const { id, name, caption } = this.props;
    return (
      <div className={`imageUploader ${this.hasImageClassName}`}>
        <label className='button imageUploader__label' htmlFor={id}>
          <img
            className='imageUploader__image'
            src={this.imageSrc}
            alt='Upload'
          />
          <input type='hidden' name={name} value={this.imageSrc} />
          <input
            className='imageUploader__input'
            id={id}
            name='image-uploader-file'
            type='file'
            accept='image/*'
            onChange={e => this.setImage(e)}
          />
          <FA className='imageUploader__noImageIcon' icon={faUpload} />
          {caption || 'Upload image'}
        </label>
      </div>
    );
  }
}

ImageUploader.propTypes = {
  id: PropTypes.string.isRequired,
  name: PropTypes.string.isRequired,
  caption: PropTypes.string,
  src: PropTypes.string,
  fileSize: PropTypes.number,
};

ImageUploader.defaultProps = {
  src: '',
};

export default ImageUploader;

import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Link, withRouter } from 'react-router-dom';
import { FontAwesomeIcon as FA } from '@fortawesome/react-fontawesome';
import { faHome, faBars } from '@fortawesome/free-solid-svg-icons';

class Navigation extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isMenuOpen: false,
    };
  }

  get currentRoute() {
    const { routes, location } = this.props;
    const route = routes.find(item => item.to === location.pathname);
    return route ? route.name : 'Home';
  }

  get routes() {
    const { routes } = this.props;
    return routes.filter(route => route.to && route.to !== '/');
  }

  toggleMenu() {
    const { isMenuOpen } = this.state;
    this.setState({ isMenuOpen: !isMenuOpen });
  }

  closeMenu() {
    this.setState({ isMenuOpen: false });
  }

  renderMenuButton() {
    const routes = this.routes;
    if (routes && routes.length) {
      return (
        <button
          className='navigation__button'
          onClick={() => this.toggleMenu()}>
          <FA icon={faBars} />
        </button>
      );
    }
  }

  renderMenu() {
    const { isMenuOpen } = this.state;
    if (isMenuOpen) {
      return this.routes.map(route => (
        <li key={route.to} className='navigation__menuItem'>
          <Link
            className='navigation__menuLink' to={route.to}
            onClick={() => this.closeMenu()}>{route.name}</Link>
        </li>
      ));
    }
  }

  render() {
    return (
      <nav className='navigation'>
        <section className='navigation__top'>
          <div className='navigation__sides'>
            <Link
              className='navigation__button' to='/'
              onClick={() => this.closeMenu()}>
              <FA icon={faHome} />
            </Link>
          </div>
          <div className='navigation__title'>{this.currentRoute}</div>
          <div className='navigation__sides'>
            {this.renderMenuButton()}
          </div>
        </section>
        <section className='navigation__menu'>
          <ul className='navigation__menuList'>{this.renderMenu()}</ul>
        </section>
      </nav>
    );
  }
}

Navigation.propTypes = {
  location: PropTypes.object,
  routes: PropTypes.array.isRequired,
};

export default withRouter(Navigation);

import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import Navigation from '../navigation/navigation.view';

class PageControl extends Component {
  renderRoutes() {
    const { routes } = this.props;
    if (routes && routes.length) {
      return routes.map(page => (
        <Route key={page.to} exact path={page.to} component={page.component} />
      ));
    }
  }

  render() {
    return (
      <Router basename={this.props.base}>
        <Navigation routes={this.props.routes} />
        {this.renderRoutes()}
      </Router>
    );
  }
}

PageControl.propTypes = {
  base: PropTypes.string,
  routes: PropTypes.array,
};

export default PageControl;
