/**
 * @external {Position} https://developer.mozilla.org/en-US/docs/Web/API/Position
 */
/**
 * @external {PositionError} https://developer.mozilla.org/en-US/docs/Web/API/PositionError
 */
/**
 * Geolocation utilities
 */
class GeolocationUtils {
  /**
   * Registers a handler function that will be called automatically each time
   * the position of the device changes. You can also, optionally, specify an
   * error handling callback function.
   *
   * @see https://developer.mozilla.org/en-US/docs/Web/API/Geolocation/watchPosition
   *
   * @param {Function} callback - Takes a {@link Position} object as an input parameter.
   * @param {Function} errorCallback - Takes a {@link PositionError} object as an input parameter.
   * @return {number} - A watch ID value that then can be used to unregister
   * the handler using {@link GeolocationUtils.disable}
   */
  static enable(callback, errorCallback) {
    return navigator.geolocation.watchPosition(callback, errorCallback);
  }

  /**
   * Gets the current position of the device.
   *
   * @see https://developer.mozilla.org/en-US/docs/Web/API/Geolocation/getCurrentPosition
   *
   * @param {Function} callback - Takes a {@link Position} object as an input parameter.
   * @param {Function} errorCallback - Takes a {@link PositionError} object as an input parameter.
   */
  static getCurrent(callback, errorCallback) {
    navigator.geolocation.getCurrentPosition(callback, errorCallback);
  }

  /**
   * unregister location/error monitoring handlers previously installed
   * using {@link GeolocationUtils.enable}
   *
   * @see https://developer.mozilla.org/en-US/docs/Web/API/Geolocation/clearWatch
   *
   * @param {number} watcher - Watch ID value returned by {@link GeolocationUtils.enable}
   */
  static disable(watcher) {
    navigator.geolocation.clearWatch(watcher);
  }

  /**
   * @ignore
   */
  static deg2rad(deg) {
    return deg * (Math.PI / 180);
  }

  /**
   * @ignore
   */
  static forceFloat(value) {
    if (typeof value === 'string') {
      return parseFloat(value);
    }
    return value;
  }

  /**
   * Calculates the distance between two points on Earth
   *
   * @param {number} lat1 - Point's A latitude
   * @param {number} lng1 - Point's A longitude
   * @param {number} lat2 - Point's B latitude
   * @param {number} lng2 - Point's B longitude
   * @return {number} - The distance in meters
   */
  static distance(lat1, lng1, lat2, lng2) {
    const fLat1 = GeolocationUtils.forceFloat(lat1);
    const fLng1 = GeolocationUtils.forceFloat(lng1);
    const fLat2 = GeolocationUtils.forceFloat(lat2);
    const fLng2 = GeolocationUtils.forceFloat(lng2);
    // Radius is in meters
    const earthRadius = 6371000;
    const dLat = GeolocationUtils.deg2rad(fLat2 - fLat1);
    const dLng = GeolocationUtils.deg2rad(fLng2 - fLng1);
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(GeolocationUtils.deg2rad(fLat1)) *
      Math.cos(GeolocationUtils.deg2rad(fLat2)) *
      Math.sin(dLng / 2) * Math.sin(dLng / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return Math.round(earthRadius * c);
  }
}

export default GeolocationUtils;

/**
 * @external {File} https://developer.mozilla.org/en-US/docs/Web/API/File
 */

/**
 * Image utilities
 */
class ImageUtils {
  /**
   * Promise that reads an image file and returns the file data when finished
   *
   * @param {File} file
   * @return {Promise<String>}
   */
  static getFileData(file) {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.addEventListener('load', ({ target }) => {
        resolve(target.result !== 'data:' ? target.result : null);
      });
      reader.addEventListener('error', error => reject(error));
      reader.readAsDataURL(file);
    });
  }
}

export default ImageUtils;

export GeolocationUtils from './geolocation';
export ImageUtils from './image';
export StringUtils from './string';

/**
 * String utilities
 */
class StringUtils {
  /**
   * Takes an object and converts it into a query string
   * @param {Object} obj
   * @return {string}
   */
  static serialize(obj) {
    return Object.keys(obj)
      .map(p => `${encodeURIComponent(p)}=${encodeURIComponent(obj[p])}`)
      .join('&');
  }
}

export default StringUtils;

import React from 'react';
import { FontAwesomeIcon as FA } from '@fortawesome/react-fontawesome';
import { faChevronDown, faChevronUp } from '@fortawesome/free-solid-svg-icons';
import Field from './field.view';
import PropTypes from 'prop-types';

/**
 * @external {HTMLOptionElement} https://developer.mozilla.org/en-US/docs/Web/API/HTMLOptionElement
 */

/**
 * Controls a dropdown type of input
 *
 * #### SCSS import:
 * ```
 * @import "~@orloxx/ui-core/scss/form/dropdown-field";
 * ```
 *
 * @extends {Field}
 *
 * @example
 * <DropdownField id='sex' name='sex' label='Sex'>
 *   <option value='M'>Male</option>
 *   <option value='F'>Female</option>
 * </DropdownField>
 */
class DropdownField extends Field {
  /**
   * @type {Object}
   * @property {Array<HTMLOptionElement>} children - Elements to show in the dropdown list
   */
  static propTypes = Object.assign({}, Field.propTypes, {
    children: PropTypes.array.isRequired,
  });

  /**
   * @ignore
   */
  constructor(props) {
    super(props);

    /**
     * @ignore
     */
    this.state = Object.assign({}, this.state, {
      isFocused: false,
      options: [],
      selectedOption: {
        id: '', label: '',
      },
    });
  }

  /**
   * @ignore
   */
  componentWillMount() {
    const options = this.props.children.map(el => ({
      id: el.props.value,
      label: el.props.children,
      selected: el.props.selected,
    }));
    const { selectedOption } = this.state;
    this.setState({
      options,
      selectedOption: options.find(el => el.selected) || selectedOption,
    });
  }

  /**
   * @ignore
   */
  componentDidUpdate(prevProps, prevState) {
    if (prevState.isFocused && !this.state.isFocused) {
      this.validate();
    }
  }

  /**
   * @ignore
   */
  onSelect(e, option) {
    e.preventDefault();
    this.setState({ selectedOption: option, isFocused: false });
  }

  /**
   * @ignore
   */
  handleFocus({ relatedTarget }) {
    const isFocused = relatedTarget &&
      (relatedTarget.classList.contains('dropdown__link') ||
        relatedTarget.classList.contains('dropdown__input') ||
        relatedTarget.classList.contains('field__icon'));
    this.setState({ isFocused: !!isFocused });
  }

  /**
   * @ignore
   */
  renderIcon() {
    return this.state.isFocused
      ? (<FA icon={faChevronUp} />)
      : (<FA icon={faChevronDown} />);
  }

  /**
   * @ignore
   */
  renderOptions() {
    if (this.state.isFocused) {
      return this.state.options.map(option => (
        <li className='dropdown__item' key={option.id}>
          <a
            className='dropdown__link' href='' title={option.label}
            onTouchEnd={e => this.onSelect(e, option)}
            onClick={e => this.onSelect(e, option)}>{option.label}</a>
        </li>
      ));
    }
    return null;
  }

  /**
   * @ignore
   */
  render() {
    const { id, label, name, placeholder, required } = this.props;
    const { selectedOption, isFocused } = this.state;
    return (
      <div className={`field dropdown ${this.fieldClasses}`} onBlur={e => this.handleFocus(e)}>
        <label className='field__label' htmlFor={id}>
          {label} {this.requiredLabel}
        </label>
        <div className='field__inputWrapper'>
          <input
            type='hidden' id={`${id}-value`}
            name={`${name}-value`} value={selectedOption.id} />
          <input
            className='field__input dropdown__input' type='text'
            id={id} name={name} ref={this.input}
            placeholder={placeholder} title={placeholder}
            required={required}
            value={selectedOption.label} readOnly
            onFocus={() => this.setState({ isFocused: true })} />
          <button
            type='button' className='field__icon'
            title={`Toggle ${label} dropdown`}
            onClick={() => this.setState({ isFocused: !isFocused })}>
            {this.renderIcon()}
          </button>
          {this.renderValidationIcon()}
        </div>
        <ul className='dropdown__list'>
          {this.renderOptions()}
        </ul>
        {this.renderValidationMessages()}
      </div>
    );
  }
}

export default DropdownField;

