import React, { Component } from 'react';
import PropTypes from 'prop-types';

/**
 * Controls the form element and validates all children when trying to submit
 *
 * @extends {react~Component}
 *
 * @example
 * <Form action='http://someaction.example.com/'>
 *   <ImageField id='avatar' name='avatar' label='Avatar' />
 *   <Field id='username' name='username'
 *     label='Username' placeholder='Enter your username'
 *     suggestion='Only accepts letters and numbers'
 *     patternError='Input format is wrong, only accepts letters and numbers'
 *     required pattern='^[a-zA-Z0-9]+$' />
 *   <Field id='name' name='name'
 *     label='Name' placeholder='Enter your full name' />
 *   <EmailField id='email' name='email'
 *     label='Email' placeholder='Enter your email address' />
 *   <DropdownField id='sex' name='sex' label='Sex' placeholder='Select one' required>
 *     <option value='M'>Male</option>
 *     <option value='F'>Female</option>
 *   </DropdownField>
 *   <TextareaField id='comments' name='comments' label='Comments'
 *     suggestion='This field has a 100 character limit'
 *     maxChars={100} maxCharsError='Character limit reached' />
 * </Form>
 */
class Form extends Component {
  /**
   * @type {Object}
   * @property {Array<HTMLOptionElement>} children - Elements to show inside the form
   * @property {String} action - The form action attribute
   * @property {Function} onSubmit - Triggers when the user submits a valid form
   * @property {Function} [onCancel] - Triggers when the user clicks the cancel button
   * @property {String} [method='GET'] - The method to use when submitting the form
   */
  static propTypes = {
    children: PropTypes.array.isRequired,
    action: PropTypes.string.isRequired,
    onSubmit: PropTypes.func.isRequired,
    onCancel: PropTypes.func,
    method: PropTypes.string,
  };

  /**
   * @ignore
   */
  static defaultProps = {
    children: [],
    method: 'GET',
  };

  /**
   * @ignore
   */
  constructor(props) {
    super(props);

    /**
     * @ignore
     */
    this.instances = {};
  }

  /**
   * @ignore
   */
  componentWillMount() {
    /**
     * @ignore
     */
    this.children = this.props.children.map(el => React.cloneElement(el, {
      key: el.props.id || Math.random().toString(36).slice(-8),
      ref: this.addChildInstance.bind(this, el),
    }));
  }
  /**
   * @ignore
   */
  addChildInstance(el, instance) {
    if (el.props.id && typeof el.type !== 'string') {
      this.instances[el.props.id] = instance;
    }
  }

  /**
   * @ignore
   */
  onSubmit(e) {
    e.preventDefault();
    const invalidFields = Object.keys(this.instances).filter((key) => {
      const component = this.instances[key];
      component.validate();
      return !component.isValid;
    });
    const { onSubmit } = this.props;
    if (onSubmit && !invalidFields.length) {
      onSubmit(e);
    }
  }

  onCancel() {
    const { onCancel } = this.props;
    if (onCancel) {
      onCancel();
    }
  }

  /**
   * @ignore
   */
  render() {
    const { action, method } = this.props;
    return (
      <form
        action={action} method={method}
        onSubmit={e => this.onSubmit(e)}
        noValidate>
        {this.children}
        <div className='buttonGroup'>
          <button
            className='button button--secondary' type='button'
            onClick={() => this.onCancel()}>Cancel</button>
          <button className='button' type='submit'>Save</button>
        </div>
      </form>
    );
  }
}

export default Form;

import React from 'react';
import PropTypes from 'prop-types';
import { FontAwesomeIcon as FA } from '@fortawesome/react-fontawesome';
import { faUpload } from '@fortawesome/free-solid-svg-icons';
import { ImageUtils } from '../../utils';
import Field from './field.view';

/**
 * Controls an input file element that shows a preview of the selected image
 *
 * #### SCSS import:
 * ```
 * @import "~@orloxx/ui-core/scss/form/image-field";
 * ```
 *
 * @extends {Field}
 *
 * @example
 * <ImageField id='image' name='image' label='Choose from computer' />
 */
class ImageField extends Field {
  /**
   * @type {Object}
   * @property {String} [src] - Default image path when nothing is selected
   * @property {number} [fileSize=256Kb] - Maximum file size (Kb) accepted
   */
  static propTypes = Object.assign({}, Field.propTypes, {
    src: PropTypes.string,
    fileSize: PropTypes.number,
  });

  /**
   * @ignore
   */
  static defaultProps = Object.assign({}, Field.defaultProps, {
    src: '',
    fileSize: 256,
  });

  /**
   * @ignore
   */
  constructor(props) {
    super(props);

    /**
     * @ignore
     */
    this.state = Object.assign({}, this.state, {
      imageData: null,
    });
  }

  /**
   * @ignore
   */
  get hasImageClassName() {
    return this.imageSrc ? 'imageField--hasImage' : '';
  }

  /**
   * @ignore
   */
  get imageSrc() {
    const { imageData } = this.state;
    const { src } = this.props;
    return imageData || src;
  }

  /**
   * @ignore
   */
  setImage(e) {
    const file = e.target.files[0];
    const maxKb = this.props.fileSize;
    const maxSize = maxKb * 1024;

    if (file && file.size > maxSize) {
      alert(`File cannot be greater than ${maxKb}Kb. Image will be removed.`);
      e.target.value = null;
      this.setState({ imageData: null });
      return;
    }
    this.setImageData(file);
  }

  /**
   * @ignore
   */
  setImageData(file) {
    if (file) {
      ImageUtils.getFileData(file)
        .then((imageData) => {
          this.setState({ imageData });
          this.validate();
        });
    }
  }

  /**
   * @ignore
   */
  render() {
    const { id, name, label } = this.props;
    return (
      <div className={`field imageField ${this.hasImageClassName}`}>
        <div className='field__inputWrapper'>
          <input
            className='field__input imageField__input'
            id={id} name={name}
            type='file'
            required={this.props.required}
            accept='image/*'
            onBlur={() => this.validate()}
            onChange={e => this.setImage(e)}
          />
          <label className='button field__label imageField__label' htmlFor={id}>
            <img
              className='imageField__image'
              src={this.imageSrc}
              alt='Upload'
            />
            <input
              type='hidden' id={`${id}-value`} name={`${name}-value`}
              ref={this.input} value={this.imageSrc} />
            <FA className='imageField__noImageIcon' icon={faUpload} />
            <span className='field__labelSpan imageField__labelSpan'>
              {label} {this.requiredLabel}
            </span>
          </label>
        </div>
        {this.renderValidationMessages()}
      </div>
    );
  }
}

export default ImageField;

